import React, { useState, useEffect } from 'react';
import './AnimatedLoginScreen.css';

export const AnimatedLoginScreen = ({ onLogin }) => {
    const [email, setEmail] = useState('kontakt+sanguivia_pl.serwer2563321@serwer2563321.home.pl');
    const [password, setPassword] = useState('nmcp-mopc-ddlr-cytl');
    const [rememberMe, setRememberMe] = useState(false);
    const [loading, setLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [showSuccess, setShowSuccess] = useState(false);

    useEffect(() => {
        // Add custom animations if not already present
        if (!document.querySelector('#neu-keyframes')) {
            const style = document.createElement('style');
            style.id = 'neu-keyframes';
            style.textContent = `
                @keyframes gentleShake {
                    0%, 100% { transform: translateX(0); }
                    25% { transform: translateX(-3px); }
                    75% { transform: translateX(3px); }
                }
                
                @keyframes successPulse {
                    0% { transform: scale(0.8); opacity: 0; }
                    50% { transform: scale(1.1); }
                    100% { transform: scale(1); opacity: 1; }
                }
            `;
            document.head.appendChild(style);
        }

        // Setup neumorphic effects (hover, ambient light)
        const setupNeumorphicEffects = () => {
            const neuElements = document.querySelectorAll('.neu-icon, .neu-checkbox, .neu-social');
            neuElements.forEach(element => {
                element.addEventListener('mouseenter', () => {
                    element.style.transform = 'scale(1.05)';
                });
                
                element.addEventListener('mouseleave', () => {
                    element.style.transform = 'scale(1)';
                });
            });
            
            document.addEventListener('mousemove', updateAmbientLight);
        };

        const updateAmbientLight = (e) => {
            const card = document.querySelector('.login-card');
            if (!card) return;
            const rect = card.getBoundingClientRect();
            const x = e.clientX - rect.left;
            const y = e.clientY - rect.top;
            
            const centerX = rect.width / 2;
            const centerY = rect.height / 2;
            
            const angleX = (x - centerX) / centerX;
            const angleY = (y - centerY) / centerY;
            
            const shadowX = angleX * 30;
            const shadowY = angleY * 30;
            
            card.style.boxShadow = `
                ${shadowX}px ${shadowY}px 60px #bec3cf,
                ${-shadowX}px ${-shadowY}px 60px #ffffff
            `;
        };

        setupNeumorphicEffects();

        return () => {
            document.removeEventListener('mousemove', updateAmbientLight);
        };
    }, []);

    const validateEmail = (inputEmail) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!inputEmail) {
            setEmailError('Email is required');
            return false;
        }
        if (!emailRegex.test(inputEmail)) {
            setEmailError('Please enter a valid email');
            return false;
        }
        setEmailError('');
        return true;
    };

    const validatePassword = (inputPassword) => {
        if (!inputPassword) {
            setPasswordError('Password is required');
            return false;
        }
        if (inputPassword.length < 6) {
            setPasswordError('Password must be at least 6 characters');
            return false;
        }
        setPasswordError('');
        return true;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const isEmailValid = validateEmail(email);
        const isPasswordValid = validatePassword(password);

        if (!isEmailValid || !isPasswordValid) {
            return;
        }

        setLoading(true);
        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 2000));
            
            if (email === 'kontakt+sanguivia_pl.serwer2563321@serwer2563321.home.pl' && password === 'nmcp-mopc-ddlr-cytl') {
                const loggedInUser = {
                    id: '1',
                    email: email,
                    fullName: 'Sanguivia Admin',
                    role: 'Owner',
                    avatar: 'SA'
                };
                setShowSuccess(true);
                setTimeout(() => {
                    onLogin(loggedInUser);
                }, 1500); // Simulate redirect delay
            } else {
                setPasswordError('Invalid credentials');
            }
        } catch (error) {
            console.error('Login error:', error);
            setPasswordError('Login failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleSocialLogin = async (provider) => {
        setLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1500));
            console.log(`Redirecting to ${provider} authentication...`);
            // In a real app, this would redirect to OAuth
        } catch (error) {
            console.error(`${provider} authentication failed:`, error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <div className="login-header">
                    <div className="neu-icon">
                        <div className="icon-inner">
                            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"/>
                                <circle cx="12" cy="7" r="4"/>
                            </svg>
                        </div>
                    </div>
                    <h2>Welcome back</h2>
                    <p>Please sign in to continue</p>
                </div>
                
                <form className="login-form" onSubmit={handleSubmit} noValidate>
                    <div className="form-group">
                        <div className="input-group neu-input">
                            <input 
                                type="email" 
                                id="email" 
                                name="email" 
                                required 
                                autoComplete="email" 
                                placeholder=" "
                                value={email}
                                onChange={(e) => { setEmail(e.target.value); validateEmail(e.target.value); }}
                                onBlur={(e) => validateEmail(e.target.value)}
                            />
                            <label htmlFor="email">Email address</label>
                            <div className="input-icon">
                                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"/>
                                    <polyline points="22,6 12,13 2,6"/>
                                </svg>
                            </div>
                        </div>
                        {emailError && <span className="error-message show">{emailError}</span>}
                    </div>

                    <div className="form-group">
                        <div className="input-group neu-input password-group">
                            <input 
                                type={showPassword ? 'text' : 'password'} 
                                id="password" 
                                name="password" 
                                required 
                                autoComplete="current-password" 
                                placeholder=" "
                                value={password}
                                onChange={(e) => { setPassword(e.target.value); validatePassword(e.target.value); }}
                                onBlur={(e) => validatePassword(e.target.value)}
                            />
                            <label htmlFor="password">Password</label>
                            <div className="input-icon">
                                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <rect x="3" y="11" width="18" height="11" rx="2" ry="2"/>
                                    <path d="M7 11V7a5 5 0 0110 0v4"/>
                                </svg>
                            </div>
                            <button 
                                type="button" 
                                className={`password-toggle neu-toggle ${showPassword ? 'show-password' : ''}`} 
                                onClick={() => setShowPassword(!showPassword)}
                                aria-label="Toggle password visibility"
                            >
                                <svg className="eye-open" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"/>
                                    <circle cx="12" cy="12" r="3"/>
                                </svg>
                                <svg className="eye-closed" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                    <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94M9.9 4.24A9.12 9.12 0 0 1 12 4c7 0 11 8 11 8a18.5 18.5 0 0 1-2.16 3.19m-6.72-1.07a3 3 0 1 1-4.24-4.24"/>
                                    <line x1="1" y1="1" x2="23" y2="23"/>
                                </svg>
                            </button>
                        </div>
                        {passwordError && <span className="error-message show">{passwordError}</span>}
                    </div>

                    <div className="form-options">
                        <div className="remember-wrapper">
                            <input 
                                type="checkbox" 
                                id="remember" 
                                name="remember"
                                checked={rememberMe}
                                onChange={(e) => setRememberMe(e.target.checked)}
                            />
                            <label htmlFor="remember" className="checkbox-label">
                                <div className="neu-checkbox">
                                    <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="3">
                                        <polyline points="20 6 9 17 4 12"/>
                                    </svg>
                                </div>
                                Remember me
                            </label>
                        </div>
                        <a href="#" className="forgot-link">Forgot password?</a>
                    </div>

                    <button type="submit" className={`neu-button login-btn ${loading ? 'loading' : ''}`} disabled={loading}>
                        <span className="btn-text">Sign In</span>
                        <div className="btn-loader">
                            <div className="neu-spinner"></div>
                        </div>
                    </button>
                </form>

                <div className="divider">
                    <div className="divider-line"></div>
                    <span>or continue with</span>
                    <div className="divider-line"></div>
                </div>

                <div className="social-login">
                    <button type="button" className="social-btn neu-social" onClick={() => handleSocialLogin('Google')} disabled={loading}>
                        <svg viewBox="0 0 24 24" fill="currentColor">
                            <path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                            <path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                            <path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
